<?php/** * Copy Right IJH.CC * Each engineer has a duty to keep the code elegant * $Id$ */if(!defined('__CORE_DIR')){    exit("Access Denied");}class Ctl_Staff_Bills extends Ctl{    public function index($page=1)    {    	$filter = $pager = array();    	$pager['page'] = max(intval($page), 1);    	$pager['limit'] = $limit = 50;        if($SO = $this->GP('SO')){            $pager['SO'] = $SO;            if(is_array($SO['dateline'])){                if($SO['dateline'][0] && $SO['dateline'][1]){                    $a = strtotime($SO['dateline'][0]);                    $b = strtotime($SO['dateline'][1])+86400;                    $filter['dateline'] = $a."~".$b;                }            }        }        if ($items = K::M('staff/bills')->items_group_by_day($filter, array('bills_sn'=>'desc'), $page, $limit, $count)) {            foreach ($items as $k=>$v){                $items[$k]['fee_amount'] = bcsub($v['freight_amount'],$v['amount'],2);            }            $pager['count'] = $count;            $pager['pagebar'] = $this->mkpage($count, $limit, $page, $this->mklink(null, array('{page}')), array('SO'=>$SO));        }        $this->pagedata['items'] = $items;        $this->pagedata['pager'] = $pager;        $this->tmpl = 'admin:staff/bills/items.html';    }    public function so($staff_id=0,$sn=0)    {        $staff_id = (int)$staff_id;        $this->pagedata['staff_id'] = $staff_id;        $this->pagedata['sn'] = $sn;        $this->tmpl = 'admin:staff/bills/so.html';    }    public function bills($sn=0, $page=1)    {        $filter = $pager = array();        $pager['page'] = max(intval($page), 1);        $pager['limit'] = $limit = 50;        if($sn = (int) $sn){            $filter['bills_sn'] = $pager['bills_sn'] = $sn;        }        if($SO = $this->GP('SO')){            $pager['SO'] = $SO;            if($SO['status']>=0){                $filter['status'] = $SO['status'];            }            if($SO['staff_id']){$filter['staff_id'] = $SO['staff_id'];}        }        if ($items = K::M('staff/bills')->items($filter, array('bills_id'=>'desc'), $page, $limit, $count)) {            $staff_ids = array();            $today = (int)date('Ymd');            foreach ($items as $k => $v) {                if($today>(int)$v['bills_sn']){                    $items[$k]['ru'] = 1;                }else{                    $items[$k]['ru'] = 0;                }                $staff_ids[$v['staff_id']] = $v['staff_id'];            }            if($staff_ids){                $this->pagedata['staff_list'] = K::M('staff/staff')->items_by_ids($staff_ids);            }            $pager['count'] = $count;            $pager['pagebar'] = $this->mkpage($count, $limit, $page, $this->mklink(null, array('{page}')), array('SO'=>$SO));        }        $this->pagedata['items'] = $items;        $this->pagedata['pager'] = $pager;        $this->pagedata['sn'] = $sn;        $this->pagedata['bills_sn_label'] = date('Y-m-d', strtotime($pager['bills_sn']));        $this->tmpl = 'admin:staff/bills/bills.html';    }    public function detail($bills_id = 0,$page)    {        if(!$bills_id = (int)$bills_id){            $this->msgbox->add('未指定要查看内容的ID', 211);        }else if(!$bills = K::M('staff/bills')->detail($bills_id)){            $this->msgbox->add('您要查看的内容不存在或已经删除', 212);        }else if(!$staff = K::M('staff/staff')->detail($bills['staff_id'])){            $this->msgbox->add('对账配送员不存在或已删除', 213);        }else{            $filter = $pager = array();            $pager['page'] = max(intval($page), 1);            $pager['limit'] = $limit = 50;            $filter['bills_id'] = $bills_id;            if($items = K::M('staff/billslog')->items($filter, array('log_id'=>'desc'), $page, $limit, $count)){                $pager['count'] = $count;                $pager['pagebar'] = $this->mkpage($count, $limit, $page, $this->mklink(null, array($bills_id,'{page}')), array('SO'=>$SO));                $order_ids = array();                foreach ($items as $k=>$v){                    $order_ids[$v['order_id']] = $v['order_id'];                }                $order_list = K::M('order/order')->items_by_ids($order_ids);                foreach ($items as $kk=>$vv){                    $items[$kk]['from'] =$order_list[$vv['order_id']]['from'];                }            }            $this->pagedata['items'] = $items;            $this->pagedata['pager'] = $pager;            $this->pagedata['bills'] = $bills;            $this->pagedata['staff'] = $staff;            $this->tmpl = 'admin:staff/bills/detail.html';        }    }    public function staff($staff_id = null)    {        if(!$staff_id = (int)$staff_id){            $this->msgbox->add('未指定要查看内容的ID', 211);        }else if(!$staff = K::M('staff/staff')->detail($staff_id)){            $this->msgbox->add('您要查看的内容不存在或已经删除', 212);        }else{            $filter = $pager = array();            $pager['page'] = max(intval($page), 1);            $pager['limit'] = $limit = 50;            $filter['staff_id'] = $staff_id;            if($SO = $this->GP('SO')){                $pager['SO'] = $SO;                if($SO['status']>=0){                    $filter['status'] = $SO['status'];                }                if(is_array($SO['dateline'])){                    if($SO['dateline'][0] && $SO['dateline'][1]){                        $a = strtotime($SO['dateline'][0]);                        $b = strtotime($SO['dateline'][1])+86400;                        $filter['dateline'] = $a."~".$b;                    }                }                if($SO['stime']&&$SO['ltime']){                    $a = strtotime($SO['stime']);                    $b = strtotime($SO['ltime'])+86399;                    $filter['dateline'] = $a."~".$b;                }                if(!$SO['stime']&&$SO['ltime']){                    $filter['dateline']  = "<:".strtotime($SO['ltime'])+86399;                }                if($SO['stime']&&!$SO['ltime']){                    $filter['dateline']  =">:".strtotime($SO['stime']);                }            }            if($items = K::M('staff/bills')->items($filter, array('bills_id'=>'desc'), $page, $limit, $count)){                $pager['count'] = $count;                $pager['pagebar'] = $this->mkpage($count, $limit, $page, $this->mklink(null, array('{page}')), array('SO'=>$SO));            }            $this->pagedata['items'] = $items;            $this->pagedata['pager'] = $pager;            $this->pagedata['staff_id'] = $staff_id;            $this->pagedata['staff'] = $staff;            $this->tmpl = 'admin:staff/bills/staff.html';        }    }    //入账    public function comfirm_bills($bill_id){        if($bills_list = $this->GP('bills_id')){            $filter = array();            $filter['bills_id'] = $bills_list;            $filter['status'] = 0;            if($items = K::M('staff/bills')->items($filter,array(),1,9999,$count)){                foreach($items as $v){                    $r_time = (int)$v['bills_sn'];                    $L_time = (int)date('Ymd');                    if($r_time>=$L_time){                        $this->msgbox->add('当前如账单不可入账',209)->response();                    }                    if(K::M('staff/bills')->update($v['bills_id'],array('status'=>1))){                        //$log_id                        if($v['amount']>0){                            $log_id = K::M('staff/staff')->update_money($v['staff_id'],$v['amount'],'您'.$v['bills_sn'].'的对账单已入账：金额:'.$v['amount'],'');                            K::M('staff/log')->update($log_id,array('extend'=>serialize(array('type'=>1,'can_id'=>$v['bills_id']))));                        }                    }else{                        $this->msgbox->add('入账失败',204)->response();                    }                }            }            $this->msgbox->add('批量入账成功');        }else{            if(!$bill_id){                $this->msgbox->add('对账单不存在',201);            }else if(!$bills = K::M('staff/bills')->detail($bill_id)){                $this->msgbox->add('对账单不存在',202);            }else if($bills['status']==1){                $this->msgbox->add('对账单已入账',203);            } else {                $time2 = (int)$bills['bills_sn'];                $time1 = (int)date('Ymd');                if($time2>=$time1){                    $this->msgbox->add('当前对账单不可入账',205)->response();                }                $amount = $bills['amount'];                if(K::M('staff/bills')->update($bill_id,array('status'=>1))){                    if($amount>0){                        $log_id = K::M('staff/staff')->update_money($bills['staff_id'],$amount,'您'.$bills['bills_sn'].'的对账单已入账：金额:'.$bills['amount'],'');                        K::M('staff/log')->update($log_id,array('extend'=>serialize(array('type'=>1,'can_id'=>$bill_id))));                    }                    $this->msgbox->add('入账成功');                }else{                    $this->msgbox->add('入账失败',204);                }            }        }    }}